import { ref, onMounted } from 'vue';

// 响应式状态

export default {
  __name: 'StudyResources',
  setup(__props, {
    expose
  }) {
    expose();
    const count = ref(0);

    // 更改状态、触发更新的函数
    function increment() {
      count.value++;
    }

    // 生命周期钩子
    onMounted(() => {
      console.log(`计数器初始值为 ${count.value}。`);
    });
    const __returned__ = {
      count,
      increment,
      ref,
      onMounted
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
